  `aantal_bijeenkomsten` int DEFAULT NULL,
  `eerste_bijeenkomst` varchar(100) DEFAULT NULL,
  `laatste_bijeenkomst` varchar(100) DEFAULT NULL,
  `inschrijven_kan_tot` varchar(100) DEFAULT NULL,
  `datum_bijeenkomst` varchar(100) DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `unique_titel` (`titel`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;
